import matplotlib.pylab as plt
import freq_analysis

LETTERS = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'

def caesar_crack(cipher_text):
    freq = freq_analysis.frequency_analysis(cipher_text)
    freq = sorted(freq.items(), key=lambda x:x[1], reverse=True)
    print(freq)
    print("The possible key value: %s" % (LETTERS.find(freq[0][0])-LETTERS.find('E')))

if __name__ == '__main__':
    cipher_text = ''
    caesar_crack(cipher_text=cipher_text)
